import axios from 'axios';
import React, { useState } from 'react';
import { useQuery } from "react-query";

const API_BASE_URL = 'http://localhost:2023'

const getKittens = async () => {
  const {data} = await axios.get(`${API_BASE_URL}/api/kittens`)
  return data
}

const Home = () => {
  const {data, status} = useQuery('kittens', getKittens, false)
  const [kat, setKat] = useState(null)

  const selectKat = (kitten) => {
    console.log('kat selected', kitten)
    setKat(kitten)
  }

  return (
    <div id="page" className=''>
      <div className='flex justify-between p-4 border-b-[1px] border-b-black-50'>
        <div className='font-bold text-2xl'>KAT Finder</div>
        <div className='flex gap-10 items-center'>
          <a href='#'>Explore</a>
          <a href='#' className='bg-blue-500 text-white font-bold px-4 py-1 rounded-[4px]'>Submit New Kitty</a>
        </div>
      </div>

      {status === 'loading' && (
        <div className='p-4 text-2xl font-bold'>
          <h2>Loading...</h2>
        </div>
      )}

      {status === 'success' && (
        <div className='p-4'>
          {/* <h2 className='text-2xl font-bold pb-6'>{data.length} Kittens</h2> */}
          <div className='flex flex-row gap-4 justify-between'>
            <div className='w-1/6 max-h-256 overflow-y-scroll'>
              {data.map((kitten) => (
                <div key={kitten.id} className='flex gap-4 p-2 items-center hover:bg-gray-100 rounded-[4px]' onClick={() => selectKat(kitten)}>
                  <img className='w-24 h-24 rounded-[4px]' src={`${API_BASE_URL}/images/${kitten.imageFileName}`} />
                  <div>
                    <p className='font-bold'>{kitten.name}</p>
                    <p className='italic text-sm'>{kitten.age}</p>
                    <p className='italic text-sm'>{kitten.gender}</p>
                  </div>
                </div>
              ))}
            </div>
            <div className='flex-1 bg-gray-100 rounded-md'>
              <div className='flex flex-col h-full w-full justify-center items-center'>
                {!kat ? (
                  <h2>Please select a Kitty first</h2>
                ) : (
                  <div className='w-1/2'>
                    <img className='rounded-[4px]' src={`${API_BASE_URL}/images/${kat.imageFileName}`} />
                    <p className='mt-10'>{kat.description}</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      <div className='text-center p-4 text-sm text-gray-400 italic'>Develop by Miguel De Leon</div>

    </div>
  )
}

export default Home
