import axios from 'axios';
import React from 'react';
import { useQuery } from "react-query";

const API_BASE_URL = 'http://localhost:2023'

const getKittens = async () => {
  const {data} = await axios.get(`${API_BASE_URL}/api/kittens`)
  console.log(data)
  return data
}

const Home = () => {
  const {data, status} = useQuery('kittens', getKittens)

  return (
    <div id="page" className=''>
      <div className='flex justify-between p-4 border-b-[1px] border-b-black-50'>
        <div className='font-bold text-2xl'>Kittens</div>
        <div className='flex gap-10 items-center'>
          <a href='#'>Explore</a>
          <a href='#'>My Account</a>
          <a href='#' className='bg-blue-500 text-white font-bold px-4 py-1 rounded-[4px]'>Submit New Kitty</a>
        </div>
      </div>

      {status === 'loading' && (
        <div className='p-4 text-2xl font-bold'>
          <h2>Loading...</h2>
        </div>
      )}

      {status === 'success' && (
        <div className='flex flex-row gap-4 justify-between p-4 text-2xl font-bold'>
          <h2>{data.length} Kittens</h2>
          {data.map((kitten) => (
            <div key={kitten.id} className='flex gap-4'>
              <img className='w-24 rounded-[4px]' src={`${API_BASE_URL}/images/${kitten.imageFileName}`} />
              {kitten.name}
            </div>
          ))}
        </div>
      )}

    </div>
  )
}

export default Home
