import axios from 'axios';
import React, { useState } from 'react';
import { useQuery } from "react-query";
import CatList from '../components/CatList';
import Header from '../components/Header';
import Loading from '../components/Loading';

const getKittens = async () => {
  const {data} = await axios.get(`${process.env.REACT_APP_API_URL}/api/kittens`)
  return data
}

const Home = () => {
  const {data, status} = useQuery('kittens', getKittens, false)
  const [kat, setKat] = useState(null)

  return (
    <div id="page" className=''>
      <Header />

      <Loading status />

      {status === 'success' && (
        <div className='p-4'>
          {/* <h2 className='text-2xl font-bold pb-6'>{data.length} Kittens</h2> */}
          <div className='flex flex-row gap-4 justify-between'>
            <CatList setKat={setKat} data={data} kat={kat} />
            {/* <div className='w-1/6 max-h-256 overflow-y-scroll'>
              {data.map((kitten) => (
                <div key={kitten.id} className={`${kat?.id === kitten.id && 'bg-yellow-200 border-yellow-400'} flex gap-4 m-2 items-center border-l-4 ${kitten.gender === 'male' ? 'border-blue-400' : 'border-rose-400' } hover:border-l-4 hover:border-yellow-400 hover:bg-yellow-200 cursor-pointer`} onClick={() => selectKat(kitten)}>
                  <img className='w-24 h-24 rounded-[4px]' src={`${process.env.REACT_APP_API_URL}/images/${kitten.imageFileName}`} />
                  <div>
                    <p className='font-bold'>{kitten.name}</p>
                    <p className='italic text-sm'>{kitten.age}</p>
                    <p className='italic text-sm'>{kitten.gender}</p>
                  </div>
                </div>
              ))}
            </div> */}

            <div className='flex-1 bg-gray-100 rounded-md'>
                {!kat ? (
                  <div className='flex flex-col h-full w-full justify-center items-center'>
                    <h2 className='font-bold text-lg'>Please select a Cat first</h2>
                  </div>
                ) : (
                  <div className='flex flex-col h-full m-auto text-center w-[400px] justify-center items-center'>
                    <div className={`p-2 mb-6 border-4 ${kat.gender == 'male' ? 'border-blue-400' : 'border-rose-400' } rounded-[200px]`}>
                      <img className='rounded-[100px] w-[200px]' src={`${process.env.REACT_APP_API_URL}/images/${kat.imageFileName}`} />
                    </div>
                    <div>
                      <div>
                        <p className='font-bold text-2xl'>{kat.name}</p>
                        <p className='italic text-sm'>{kat.age} - {kat.gender}</p>

                      </div>
                      <p className='mt-4'>{kat.description}</p>
                    </div>
                  </div>
                )}
            </div>
          </div>

        </div>
      )}

      <div className='text-center p-4 text-sm text-gray-400 italic'>Develop by Miguel De Leon</div>

    </div>
  )
}

export default Home
